var oNot=new GlideRecord('cmn_notif_device');
oNot.initialize();
oNot.name="Personal Email";
oNot.type="Email";
oNot.email_address=current.u_personal_email;//personal email id
oNot.order=2;
oNot.active=true;
oNot.user=current.sys_id;
oNot.insert();

var sGroupName=current.department.getDisplayValue()+
	            "_"+current.u_workspace+"_Users";
var oGroup=new GlideRecord('sys_user_group');
if(oGroup.get(sGroupName)){
	var qR = new GlideRecord('sys_user_grmember');
	qR.addQuery('user',current.sys_id);
	qR.addQuery('group',oGroup.sys_id);
	qR.query();
	if(!qR.next()){
		//no prior relationship exists
		var oRel = new GlideRecord('sys_user_grmember');
		oRel.initialize();
		oRel.user=current.sys_id;
		oRel.group=oGroup.sys_id;
		oRel.insert();
		//so we add a relationship
	}
	else{
		//not supposed to happen but we don't care
		gs.addInfoMessage("Relation already exists between user: "+
						  current.user_name+
						  " and group: "+
						  oGroup.getDisplayValue()
						  );
	}
	
}
else{
	//groups are supposed to exist after initial AD sync
	gs.addErrorMessage("Group : "+sGroupName+" doesn't exist");
}
workflow.scratchpad.user_id=current.sys_id;
workflow.scratchpad.personal_email=current.u_personal_email;
